mapper.select:
SELECT 

FROM vpl_order_delivery a

WHERE a.id = #{id}

SELECT 

FROM vpl_order_delivery a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.cus_name LIKE concat('%', #{cusName},'%')






		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM vpl_order_delivery a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE vpl_order_delivery SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
